            }
            return true;
        });
    });

    msg.payload = { success: true, data: filtered };
    msg.statusCode = 200;
} catch (err) {
    msg.payload = { success: false, error: err.message || 'Failed to load paper metadata' };
    msg.statusCode = 500;
    node.error(err.message);
}

return msg;

