function cleanText(s){
  s = String(s == null ? '' : s);
  // normalize CR to LF
  s = s.replace(/\r\n?/g, '\n');
  // strip ASCII control chars except tab/newline
  s = s.replace(/[\u0000-\u0008\u000B\u000C\u000E-\u001F]/g, ' ');
  // remove JS line and paragraph separators
